import React, { useState } from 'react'
import InputGroup from '../../../components/InputGroup'
import api from '../../../utils/api'

function AddPet() {
    const [pet, setPet] = userState({})
    const [preview, setPreview] = useState()
    const [token] = useState(localStorage.getItem('token') || '')

    function handleChange(e) {
        setPet({ ...pet, [e.target.name]: e.target.value })
    }
    const[image, setImage] = useState(null)
    function onFileChange(e){
        setPreview(URL.createObjectURL(e.target.files[0]))
        setImage(e.target)
    }
    function handleSubmit(e) {
        e.preventDefault()
    }
    return (
        <div>
            <h3>Cadastre um Pet para adoção</h3>
            <form onSubmit={handleSubmit}>
                <InputGroup
                    type='file'
                    label='Colocar a foto do dog'
                    name='image'
                    handleChange={handleChange}
                />
                <InputGroup
                    type='text'
                    label='Digite o nome o dog'
                    name='name'
                    placeholder='Digite o nome do cachorro'
                    handleChange={handleChange}
                />
                <InputGroup
                    type='number'
                    label='Digite a idade do dog'
                    name='age'
                    placeholder='Digite a idade do cachorro'
                    handleChange={handleChange}
                />
                <InputGroup
                    type='number'
                    label='Digite o peso do dog'
                    name='weigth'
                    placeholder='Digite o peso do cachorro'
                    handleChange={handleChange}
                />
                <InputGroup
                    type='text'
                    label='Digite a cor do cachorro'
                    name='color'
                    placeholder='Digite a cor do cachorro'
                    handleChange={handleChange}
                />
            </form>
        </div>
    )
}
export default AddPet
